import React, { useState, useEffect } from 'react';

const App = () => {
  const [user, setUser] = useState([])
  const [name, setName] = useState('')
  const [pass, setPass] = useState('')

  /*   useEffect(() => {
      let token = new Promise((re, reg) => {
        let AuthToken = { username: 'test_super', password: 'Nf<U4f<rDbtDxAPn' }

        fetch('http://emphasoft-test-assignment.herokuapp.com/api-token-auth/', { method: 'POST', body: JSON.stringify(AuthToken), headers: { 'Content-Type': 'application/json' } })
          .then(e => re(e.json()))
          .catch(e => reg(e))
      })

      token.then(e => {
        fetch(`http://emphasoft-test-assignment.herokuapp.com/api/v1/users`, { method: 'GET', headers: { 'Authorization': `Token ${e.token}` } })
          .then(e2 => e2.json())
          .then(e2 => setUser(e2))
          .catch(e2 => console.log(e2))
      })
    }, [user]) */


  const btnAuth = () => {
    if (name !== ' ' & name !== '' & name === 'test_super') {
      if (pass.length >= 8 && pass.includes(pass[0].toUpperCase()) && pass.replace(/\D/g, '').length > 0 & pass === 'Nf<U4f<rDbtDxAPn') {
        let token = new Promise((re, reg) => {
          let AuthToken = { username: 'test_super', password: 'Nf<U4f<rDbtDxAPn' }

          fetch('http://emphasoft-test-assignment.herokuapp.com/api-token-auth/', { method: 'POST', body: JSON.stringify(AuthToken), headers: { 'Content-Type': 'application/json' } })
            .then(e => re(e.json()))
            .catch(e => reg(e))
        })

        token.then(e => {
          fetch(`http://emphasoft-test-assignment.herokuapp.com/api/v1/users`, { method: 'GET', headers: { 'Authorization': `Token ${e.token}` } })
            .then(e2 => e2.json())
            .then(e2 => setUser(e2))
            .catch(e2 => console.log(e2))
        })
      }
      else {
        alert('Пароль введён не правильно! Пароль должен быть с заглавной буквы, иметь длину не менее 8 символов, и содержать хотя бы 1 цифру')
      }
    }
    else {
      alert('Пользователь не найден')
    }


    return
    let nameUser = user.filter((v) => v.username === name)
    let passUser = user.filter((v) => v.password === pass)
    console.log(nameUser);
    console.log(passUser);
  }

  return (
    <div>
      <div style={{ display: "flex", flexWrap: "wrap", gap: '1rem' }}>
        {user.map((v, i) => <Users {...v} key={i} />)}
      </div>
      <input defaultValue='test_super' placeholder='name' type='text' onChange={e => setName(e.target.value)} />
      <input defaultValue='Nf<U4f<rDbtDxAPn' placeholder='password' type='password' onChange={e => setPass(e.target.value)} />

      <button onClick={() => btnAuth()}>Auth</button>
    </div>
  );
}

const Users = ({ id, username, first_name, last_name, password, is_active, last_login, is_superuser }) => {

  return (
    <>
      <p>{/* {id} */} {username} {/* {first_name} {last_name} */} {password} {/* {is_active} {last_login} {is_superuser} */}</p>
    </>
  )
}

export default App;
